// Get raw text from AI output (adjust the key if needed)
const raw = items[0].json.completion || items[0].json.text;

// Parse JSON string
let parsed;
try {
  parsed = JSON.parse(raw);
} catch (e) {
  throw new Error("AI did not return valid JSON: " + raw);
}

// Expand each script as its own item
return parsed.map(obj => ({ json: obj }));

return items.map(item => {
  const key = item.json.Key;
  return {
    json: {
      ...item.json, // keep all previous data such as row_number, title, etc.
      key,
      public_url: `https://fayncqfscwtuckxqzmcv.supabase.co/storage/v1/object/public/${key}`
    }
  };
});

